insert into dynamic_form_fields_validation(df_id,form_type,transaction_type,validation_type,validation_rule,validation_message) 
select df_id,form_type,transaction_type,'required','required',replace(field_label,'*','')||' Required' FROM public.dynamic_form_fields where field_is_required ='true';

insert into dynamic_form_fields_validation(df_id,form_type,transaction_type,validation_type,validation_rule,validation_message) 
select df_id,form_type,transaction_type,'pattern','^([0-2][0-9]|(3)[0-1])(\/)(((0)[0-9])|((1)[0-2]))(\/)\d{4}$',replace(field_label,'*','')||' Should be a Date' FROM public.dynamic_form_fields where field_type ='date';

insert into dynamic_form_fields_validation(df_id,form_type,transaction_type,validation_type,validation_rule,validation_message) 
select df_id,form_type,transaction_type,'pattern','^[0-9]+(\.[0-9]{1,2})?$',replace(field_label,'*','')||' Should be a Date' FROM public.dynamic_form_fields where field_type ='amount';	
